{
    "tiddlers": {
        "$:/plugins/spangenhelm/enhancedsearch/searchtabmanager.js": {
            "text": "/*\
title: $:/plugins/spangenhelm/enhancedsearch/searchtabmanager.js
type: application/javascript
module-type: macro
\*/

(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports.name = "search-tab-manager";
exports.params = [ { name : "searchTerm" }, { name: "qualifier" }  ];

// constants
var sideBarTabPrefix = "$:/state/tab/sidebar-";
var searchTabTRef = "$:/core/ui/SearchResults";
var tabBackup = null;

/**
 * Called: <$macrocall $name="search-tab-manager" searchTerm={{$:/temp/search}} />
 */
exports.run = function(searchTerm, qualifier) {

  var tabsStateTRef = sideBarTabPrefix + this.getStateQualifier();    
  var tabsStateTObj = $tw.wiki.getTiddler(tabsStateTRef);

  console.log("sr:", tabsStateTObj);

  if(tabsStateTObj) {

    if(searchTerm && tabsStateTObj.fields.text !== searchTabTRef) {

      // backup old tab
      tabBackup = tabsStateTObj.fields.text;

      // set tab to search results
      $tw.wiki.addTiddler(new $tw.Tiddler({
        title: tabsStateTRef,
        text: searchTabTRef
      }));

    } else if(!searchTerm && tabsStateTObj.fields.text === searchTabTRef && tabBackup) {

      // set tab to search results
      $tw.wiki.addTiddler(new $tw.Tiddler({
        title: tabsStateTRef,
        text: tabBackup
      }));

    }

  }

  // always return an empty string
  return "";

};

})();
",
            "title": "$:/plugins/spangenhelm/enhancedsearch/searchtabmanager.js",
            "type": "application/javascript",
            "module-type": "macro"
        },
        "$:/core/ui/SideBarLists": {
            "title": "$:/core/ui/SideBarLists",
            "text": "<div class="tc-sidebar-lists">
<div class="tc-search">
<$edit-text tiddler="$:/temp/search" type="search" tag="input" focus="true"/>
<$macrocall $name="search-tab-manager" searchTerm={{$:/temp/search}} />
<$reveal state="$:/temp/search" type="nomatch" text="">
<$button tooltip={{$:/language/Buttons/AdvancedSearch/Hint}} aria-label={{$:/language/Buttons/AdvancedSearch/Caption}} class="tc-btn-invisible">
<$action-setfield $tiddler="$:/temp/advancedsearch" text={{$:/temp/search}}/>
<$action-setfield $tiddler="$:/temp/search" text=""/>
<$action-navigate $to="$:/AdvancedSearch"/>
{{$:/core/images/advanced-search-button}}
</$button>
<$button class="tc-btn-invisible">
<$action-setfield $tiddler="$:/temp/search" text="" />
{{$:/core/images/close-button}}
</$button>
</$reveal>
<$reveal state="$:/temp/search" type="match" text="">
<$button to="$:/AdvancedSearch" tooltip={{$:/language/Buttons/AdvancedSearch/Hint}} aria-label={{$:/language/Buttons/AdvancedSearch/Caption}} class="tc-btn-invisible">
{{$:/core/images/advanced-search-button}}
</$button>
</$reveal>
</div>

<$reveal state="$:/temp/search" type="nomatch" text="">

<$set name="searchTiddler" value="$:/temp/search">
<$macrocall $name="tabs" tabsList="$:/core/ui/SearchResults[all[shadows+tiddlers]tag[$:/tags/SideBar]!has[draft.of]]"  default="$:/core/ui/SearchResults" state="$:/state/tab/sidebar" />
</$set>

</$reveal>

<$reveal state="$:/temp/search" type="match" text="">

<$macrocall $name="tabs" tabsList="[all[shadows+tiddlers]tag[$:/tags/SideBar]!has[draft.of]]"  default={{$:/config/DefaultSidebarTab}} state="$:/state/tab/sidebar" />

</$reveal>
</div>"
        },
        "$:/core/ui/SearchResults": {
            "title": "$:/core/ui/SearchResults",
            "caption": "{{$:/language/SideBar/Search/Caption}}",
            "text": "<div class="tc-search-results">
<$list filter="[all[shadows+tiddlers]tag[$:/tags/SearchResults]!has[draft.of]butfirst[]limit[1]]" emptyMessage="""
<$list filter="[all[shadows+tiddlers]tag[$:/tags/SearchResults]!has[draft.of]]">
<$transclude mode="block"/>
</$list>
""">
<$macrocall $name="tabs" tabsList="[all[shadows+tiddlers]tag[$:/tags/SearchResults]!has[draft.of]]" default={{$:/config/SearchResults/Default}}/>
</$list>
</div>
"
        },
        "$:/language/SideBar/Search/Caption": {
            "title": "$:/language/SideBar/Search/Caption",
            "text": "Search"
        },
        "$:/plugins/spangenhelm/enhancedsearch/readme": {
            "title": "$:/plugins/spangenhelm/enhancedsearch/readme",
            "text": "This plugin allows you to have the search-results displayed in a tab without hiding other existing tabs in the sidebar.
"
        },
        "$:/plugins/spangenhelm/enhancedsearch/stylesheet": {
            "title": "$:/plugins/spangenhelm/enhancedsearch/stylesheet",
            "tags": "$:/tags/Stylesheet",
            "type": "text/css",
            "text": "div.tc-search-results {
   padding-top: 0;
}
"
        }
    }
}
